/**
 * Simple Fetch-based HTTP client with retry logic
 */
export async function httpClient<T>(
  url: string,
  options: RequestInit & { retries?: number; backoff?: number } = {}
): Promise<T> {
  const { retries = 3, backoff = 1000, ...fetchOptions } = options;

  let lastError: Error | null = null;

  for (let attempt = 1; attempt <= retries; attempt++) {
    try {
      const response = await fetch(url, {
        ...fetchOptions,
        headers: {
          "Content-Type": "application/json",
          ...fetchOptions.headers,
        },
      });

      if (!response.ok) {
        if (response.status >= 500 && attempt < retries) {
          throw new Error(`Server error: ${response.status}`);
        }
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || `HTTP error! status: ${response.status}`);
      }

      return (await response.json()) as T;
    } catch (error: any) {
      lastError = error;
      if (attempt < retries) {
        const delay = backoff * Math.pow(2, attempt - 1);
        console.warn(`[HTTP] Attempt ${attempt} failed, retrying in ${delay}ms...`);
        await new Promise((resolve) => setTimeout(resolve, delay));
      }
    }
  }

  throw lastError || new Error("Request failed");
}

import { Elysia, t } from "elysia";
import { swagger } from "@elysiajs/swagger";

const app = new Elysia()
  .use(swagger({
    documentation: {
      info: {
        title: "Elysia Production API",
        version: "1.0.0",
      }
    }
  }))
  // Global error handling
  .error({
    BAD_REQUEST: 400,
    UNAUTHORIZED: 401,
    FORBIDDEN: 403,
    NOT_FOUND: 404,
    INTERNAL_SERVER_ERROR: 500
  })
  .onError(({ code, error, set }) => {
    console.error(`[${code}] ${error.message}`);

    return {
      success: false,
      code,
      message: process.env.NODE_ENV === "production" && code === "INTERNAL_SERVER_ERROR"
        ? "Internal Server Error"
        : error.message,
    };
  })
  // Health check
  .get("/health", () => ({
    status: "ok",
    uptime: process.uptime(),
    timestamp: new Date().toISOString(),
  }))
  .get("/", () => ({ message: "Hello Elysia" }))
  .listen(process.env.PORT || 3000);

console.log(
  `Elysia is running at ${app.server?.hostname}:${app.server?.port}`
);

// Graceful shutdown (Bun native)
process.on("SIGTERM", () => {
  console.log("SIGTERM received. Cleaning up...");
  app.stop();
  process.exit(0);
});

process.on("SIGINT", () => {
  console.log("SIGINT received. Cleaning up...");
  app.stop();
  process.exit(0);
});

import { Elysia } from "elysia";
import { cors } from "@elysiajs/cors";

export const setupCors = (app: Elysia) => {
  return app.use(
    cors({
      origin: "http://localhost:3000",
      methods: ["GET", "POST", "PUT", "DELETE", "OPTIONS"],
      allowedHeaders: ["Content-Type", "Authorization"],
      credentials: true,
    })
  );
};

import { Elysia } from "elysia";
import { jwt } from "@elysiajs/jwt";

export const setupJwt = (app: Elysia) => {
  return app.use(
    jwt({
      name: "jwt",
      secret: process.env.JWT_SECRET || "elysia-is-awesome",
    })
  );
};

import { Elysia } from "elysia";
import { swagger } from "@elysiajs/swagger";

export const setupSwagger = (app: Elysia) => {
  return app.use(
    swagger({
      documentation: {
        info: {
          title: "Hanma Documentation",
          version: "1.0.0",
        },
      },
    })
  );
};
