snippets.usage:
import { setupBearer, requireBearer } from './plugins/bearer';

app.use(setupBearer())
   .get('/protected', () => {}, { beforeHandle: [requireBearer] });

import { setupStatic, setupSPA } from './plugins/static';

app.use(setupStatic({ prefix: '/public' }));
// Or for SPA:
app.use(setupSPA({ assets: 'dist' }));

import { setupHtml, htmlTemplate } from './plugins/html';

app.use(setupHtml())
   .get('/', ({ html }) => html('<h1>Hello</h1>'));

import { createSSEResponse } from './plugins/stream';

app.get('/events', () => createSSEResponse(async (sse) => {
  sse.send({ message: 'Hello' }, 'greeting');
}));
