import { healthRoutes, commonChecks } from './routes/health';

app.use(healthRoutes({
  version: process.env.npm_package_version,
  checks: {
    database: commonChecks.database(prisma),
    memory: commonChecks.memory(500),
  },
}));

// GET /health, /health/live, /health/ready

import { createCrudRoutes } from './routes/crud';

app.use(createCrudRoutes({
  prefix: '/users',
  entityName: 'User',
  entitySchema: UserSchema,
  createSchema: CreateUserSchema,
  updateSchema: UpdateUserSchema,
  service: userService,
}));

// GET /users, GET /users/:id, POST /users, PUT/PATCH/DELETE /users/:id
