import { parseOffsetParams, buildOffsetResponse } from './utils/pagination';

app.get('/users', async ({ query }) => {
  const { page, limit, offset } = parseOffsetParams(query);
  const [users, total] = await Promise.all([...]);
  return buildOffsetResponse(users, total, page, limit);
});

import { success, errors } from './utils/response';

app.get('/users/:id', ({ params }) => {
  const user = findUser(params.id);
  if (!user) return errors.notFound('User not found');
  return success(user);
});

import { createEnv, envPatterns } from './utils/env';

const env = createEnv({
  PORT: envPatterns.port(3000),
  DATABASE_URL: { type: 'string', required: true },
  JWT_SECRET: envPatterns.secret(true),
});

app.listen(env.PORT);

import { hashPassword, verifyPassword, generateToken } from './utils/crypto';

const hash = await hashPassword('secret');
const isValid = await verifyPassword('secret', hash);
const token = generateToken(32);

