import { str, num, schemas, compose } from './validation/typebox';

const UserSchema = t.Object({
  email: str.email(),
  age: num.positiveInt(),
});

const CreateUserSchema = compose.omit(UserSchema, ['id']);

import { z } from 'zod';
import { validateBody, zodSchemas } from './validation/zod';

const CreateUserSchema = z.object({
  email: zodSchemas.email,
  password: z.string().min(8),
});

app.post('/users', () => {}, { beforeHandle: [validateBody(CreateUserSchema)] });
